    <script>
        function getCarouselData() {
            return {
                currentIndex: 0,
                images: [
                    'data:image/png;base64,',
                    'data:image/jpeg;base64,',
                    'data:image/png;base64,',
                    'data:image/png;base64,',
                    'data:image/png;base64,',
                    'data:image/png;base64,',
                    'data:image/png;base64,',
                    'data:image/jpeg;base64,',
                    'data:image/png;base64,',
                ],
                increment() {
                    this.currentIndex = this.currentIndex === this.images.length - 6 ? 0 : this.currentIndex + 1;
                },
                decrement() {
                    this.currentIndex = this.currentIndex === this.images.length - 6 ? 0 : this.currentIndex - 1;
                },
            }
        }
    </script>
